mapper.update:
UPDATE t_parklot_amount pa -- 已发布车位数 SET pa.publish_amount = (SELECT count(DISTINCT tpc.id) from t_publish p INNER JOIN t_parkloc tpc ON tpc.id=p.parkloc_id WHERE tpc.parklot_id=#{parklotId} AND tpc.state<>0 AND p.state<>0), -- 已预约车位数 等于 预约阶段的车位数 加上 正在停车阶段的车位数 pa.reserved_amount = (SELECT count(DISTINCT tpc.id) from t_order_parking top INNER JOIN t_parkloc tpc ON tpc.id=top.parkloc_id WHERE tpc.parklot_id=#{parklotId} AND tpc.state<>0 AND top.state in (1300,1301) AND top.id NOT IN(SELECT reserve_id from t_order_parking WHERE reserve_id IS NOT NULL AND parklot_id=top.parklot_id))
+ (SELECT count(DISTINCT tpc.id) from t_order_parking top INNER JOIN t_parkloc tpc ON tpc.id=top.parkloc_id WHERE tpc.parklot_id=#{parklotId} AND tpc.state<>0 AND top.state in (1302) AND top.reserve_id IS NOT NULL),
-- 可预约车位数 pa.reservable_amount= (SELECT COUNT(DISTINCT tpc.id) FROM t_share ts INNER JOIN t_parkloc tpc ON ts.parkloc_id=tpc.id WHERE tpc.parklot_id=#{parklotId} AND tpc.state =1101 and ts.state=1 AND ( ((ts.end_time - #{now}) > #{timeInterval} AND ts.start_time<=#{now}) OR ((ts.start_time - #{now}) <= #{advanceReservationTimeVALUE} AND ts.start_time >=#{now}) )), pa.idle_amount = pa.publish_amount - pa.reserved_amount, pa.modify_time = #{now} WHERE pa.parklot_id = #{parklotId};

mapper.select:

SELECT COUNT(DISTINCT tpc.id) FROM t_share ts INNER JOIN t_parkloc tpc ON ts.parkloc_id=tpc.id
       WHERE tpc.parklot_id=#{parklotId}
           AND tpc.state =1101 AND ts.state=1
   AND (
      ((ts.end_time - #{now}) > #{timeInterval}
       AND ts.start_time<=#{now})
  OR
      ((ts.start_time - #{now}) <= #{advanceReservationTimeVALUE} AND ts.start_time >=#{now}
      AND (ts.end_time - #{now}) > #{timeInterval})
  )


SELECT DISTINCT tpc.id FROM t_share ts INNER JOIN t_parkloc tpc ON ts.parkloc_id=tpc.id
       WHERE tpc.parklot_id=#{parklotId}
           AND tpc.state =1101 AND ts.state=1
   AND (
      ((ts.end_time - #{now}) > #{timeInterval}
       AND ts.start_time<=#{now})
  OR
      ((ts.start_time - #{now}) <= #{advanceReservationTimeVALUE} AND ts.start_time >=#{now}
      and (ts.end_time - #{now}) > #{timeInterval})
  )
